//var logoData = "data:image/png;base64,";
//var logoUrl = "http://cn.cocos2d-x.org/image/logo.png";
var logoUrl = "res/BattleCity.png";

//自定义加载进度
var Loading = cc.Scene.extend({
    _interval : null,
    _label : null,
    _className:"Loading",
    _length : 0,  
    _count : 0,  
    _winSize : null,   
    _processLayer: null,   
    _processLayerLength: null,  
    
    /**
     * Contructor of Loading
     * @returns {boolean}
     */
    init : function(){
        var self = this;

        //logo
        var logoWidth = 160;
        var logoHeight = 200;

        // bg
        var bgLayer = self._bgLayer = new cc.LayerColor(cc.color(32, 32, 32, 255));
        self.addChild(bgLayer, 0);

        //image move to CCSceneFile.js
        var fontSize = 24, lblHeight =  -logoHeight / 2 + 100;

        cc._loaderImage = logoUrl;
        //if(cc._loaderImage){
        if (logoUrl){
            //loading logo
            /*
            cc.loader.loadImg(cc._loaderImage, {isCrossOrigin : false }, function(err, img){
                logoWidth = img.width;
                logoHeight = img.height;
                self._initStage(img, cc.visibleRect.center);
            });
             */
            //加载本地（远程）图片
            cc.textureCache.addImage(logoUrl, this.logoLoaded, this);

            fontSize = 14;
            lblHeight = -logoHeight / 2 - 10;
        }
        //loading percent
        var label = self._label = new cc.LabelTTF("Loading... 0%", "Arial", fontSize);
        label.setPosition(cc.pAdd(cc.visibleRect.center, cc.p(0, lblHeight)));
        label.setColor(cc.color(180, 180, 180));
        bgLayer.addChild(this._label, 10);

        // 定义进度条层  
        this._winSize = cc.director.getWinSize();  
        var centerPos = cc.p(this._winSize.width / 2, this._winSize.height / 2);  
        self._processLayerLength = 500;  
        self._processLayer = cc.LayerColor.create(cc.color(255, 100, 100, 128), 1, 30);  
        self._processLayer.setPosition(cc.pAdd(centerPos, cc.p(- this._processLayerLength / 2, -logoHeight / 2 - 50)));  
        bgLayer.addChild(this._processLayer);       

        return true;
    },

    _initStage: function (img, centerPos) {
        var self = this;
        var texture2d = self._texture2d = new cc.Texture2D();
        texture2d.initWithElement(img);
        texture2d.handleLoadedTexture();
        var logo = self._logo = new cc.Sprite(texture2d);
        logo.setScale(cc.contentScaleFactor());
        logo.x = centerPos.x;
        logo.y = centerPos.y;
        self._bgLayer.addChild(logo, 10);
    },

    logoLoaded: function(texture) {
        if (texture instanceof cc.Texture2D) {
            //cc.log("Remote texture loaded: " + logoUrl);
            if (this.logo) {
                this.removeChild(this.logo);
            }
            this.logo = new cc.Sprite(texture);
            this.logo.x = cc.winSize.width/2;
            this.logo.y = cc.winSize.height/2;
            this.addChild(this.logo);
        }
        else {
            cc.log("Fail to load remote texture");
        }
    },

    /**
     * custom onEnter
     */
    onEnter: function () {
        var self = this;
        cc.Node.prototype.onEnter.call(self);
        self.schedule(self._startLoading, 0.3);
    },
    /**
     * custom onExit
     */
    onExit: function () {
        cc.Node.prototype.onExit.call(this);
        var tmpStr = "Loading... 0%";
        this._label.setString(tmpStr);
    },

    /**
     * init with resources
     * @param {Array} resources
     * @param {Function|String} cb
     */
    initWithResources: function (resources, cb) {
        if(cc.isString(resources))
            resources = [resources];
        this.resources = resources || [];
        this.cb = cb;
    },

    /*
    _startLoading: function () {
        var self = this;
        self.unschedule(self._startLoading);
        var res = self.resources;
        cc.loader.load(res,
                function (result, count, loadedCount) {
            var percent = (loadedCount / count * 100) | 0;
            percent = Math.min(percent, 100);
            self._label.setString("Loading... " + percent + "%");
        }, function () {
            if (self.cb)
                self.cb();
        });
    },
     */

    _startLoading: function () {  
        var self = this;  
        self.unschedule(self._startLoading);  
        var res = self.resources;  
        self._length = res.length;  
        self._count = 0;  
        cc.loader.load(res, function(result, count){ self._count = count; }, function(){  
            if(self.cb)  
                self.cb();  
        });  
        self.schedule(self._updatePercent);  
    },  


    _updatePercent: function () {  
        var self = this;  
        var count = self._count;  
        var length = self._length;  
        var percent = (count / length * 100) | 0;  
        percent = Math.min(percent, 100);  
        self._label.setString("Loading... " + percent + "%");  

        // 更新进度条的长度  
        this._processLayer.changeWidth(this._processLayerLength * percent / 100);  

        if(count >= length) self.unschedule(self._updatePercent);      
    }  

});
/**
 * <p>Loading.preload can present a myLoading with download progress.</p>
 * <p>when all the resource are downloaded it will invoke call function</p>
 * @param resources
 * @param cb
 * @returns {Loading|*}
 * @example
 * //Example
 * Loading.preload(g_resources, function () {
        cc.director.runScene(new HelloWorldScene());
    }, this);
 */
Loading.preload = function(resources, cb){
    var myLoading = null;
    if(!myLoading) {
        myLoading = new Loading();
        myLoading.init();
    }
    myLoading.initWithResources(resources, cb);

    cc.director.runScene(myLoading);
    return myLoading;
};